import React from "react";
import SignUpForm from "../assets/styles/SignUpForm.style";

export default function SignUpFormComponent(params) {
  const [userEmail, setUserEmail] = React.useState();
  const [userPassword, setUserPassword] = React.useState();
  const [username, setUsername] = React.useState();
  const [userPictureUrl, setUserPictureUrl] = React.useState();

  function handleSubmit(e){
    e.preventDefault();
    if(!userEmail || !userPassword || !username || !userPictureUrl){
        alert("Campos não preenchidos, ou preenchidos incorretamente!")
    } else{
        alert("tudo certo!")
    }
  };

  return (
    <SignUpForm>
      <div>
        <input
          placeholder="e-mail"
          onChange={(e) => setUserEmail(e.target.value)}
        />

        <input
          placeholder="password"
          type={"password"}
          onChange={(e) => setUserPassword(e.target.value)}
        />

        <input
          placeholder="name"
          onChange={(e) => setUsername(e.target.value)}
        />

        <input
          placeholder="picture url"
          type={"url"}
          onChange={(e) => setUserPictureUrl(e.target.value)}
        />

        <input
          className="submitButton"
          type={"submit"}
          value="Sign Up"
          onClick={e => handleSubmit(e)}
        />
      </div>

      <span>Switch back to log in</span>

      <span>
        {userEmail}
        {userPassword}
        {username}
        {userPictureUrl}
      </span>

    </SignUpForm>
  );
}
